begin
wwv_flow_imp_shared.create_automation(
 p_id=>wwv_flow_imp.id(19422663755852080)
,p_name=>'Refresh OAuth Token'
,p_static_id=>'refresh-oauth-token'
,p_trigger_type=>'POLLING'
,p_polling_interval=>'FREQ=MINUTELY;INTERVAL=1'
,p_polling_status=>'DISABLED'
,p_result_type=>'ROWS'
,p_location=>'LOCAL'
,p_use_local_sync_table=>false
,p_query_type=>'SQL'
,p_query_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'select 1 from (SELECT ',
'    (EXTRACT(SECOND FROM (EXPIRY_IN - SYSTIMESTAMP)) +',
'    EXTRACT(MINUTE FROM (EXPIRY_IN - SYSTIMESTAMP)) * 60 +',
'    EXTRACT(HOUR   FROM (EXPIRY_IN - SYSTIMESTAMP)) * 3600 +',
'    EXTRACT(DAY    FROM (EXPIRY_IN - SYSTIMESTAMP)) * 86400 )sec',
'  FROM AUTH_HISTORY ',
'  WHERE ID = (SELECT MAX(ID) FROM AUTH_HISTORY)) where sec <=300 and sec > 0'))
,p_include_rowid_column=>false
,p_commit_each_row=>true
,p_error_handling_type=>'ABORT'
);
wwv_flow_imp_shared.create_automation_action(
 p_id=>wwv_flow_imp.id(19422941008852081)
,p_automation_id=>wwv_flow_imp.id(19422663755852080)
,p_name=>'GetNewAccessToken'
,p_execution_sequence=>10
,p_action_type=>'NATIVE_PLSQL'
,p_action_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'BEGIN ',
'    DECLARE',
'        l_url            VARCHAR2(4000) := ''https://login.microsoftonline.com/d6a54750-2e86-4d95-8dce-6624db1fe568/oauth2/v2.0/token'';',
'        l_response       CLOB;',
'        l_body           VARCHAR2(4000);',
'        l_http_status_code NUMBER;',
'        l_access_token   VARCHAR2(4000);',
'        l_expires_in     NUMBER;',
'        l_token_expiry   TIMESTAMP;',
'    	',
'    BEGIN',
'',
'    	-- 1. Set HTTP Headers',
'            apex_web_service.g_request_headers.DELETE;',
'            apex_web_service.g_request_headers(1).name := ''Content-Type'';',
'            apex_web_service.g_request_headers(1).value := ''application/x-www-form-urlencoded'';',
'',
'            -- 2. Prepare payload',
'            l_body := ''client_id='' || APEX_UTIL.URL_ENCODE(''a059e8aa-82d8-4fb1-8aeb-e0e3b55add2f'') ||',
'                      ''&client_secret='' || APEX_UTIL.URL_ENCODE(''4sU8Q~LHabB5Mk6Mlnu5uzpmaW1dDEdPouV4-bYk'') ||',
'                      ''&grant_type='' || APEX_UTIL.URL_ENCODE(''client_credentials'') ||',
'                      ''&scope='' || APEX_UTIL.URL_ENCODE(''https://api.businesscentral.dynamics.com/.default'');',
'',
'            -- 3. Make REST call',
'            l_response := APEX_WEB_SERVICE.MAKE_REST_REQUEST(',
'                p_url         => l_url,',
'                p_http_method => ''POST'',',
'                p_body        => l_body',
'            );',
'',
'            l_http_status_code := APEX_WEB_SERVICE.g_status_code;',
'',
'            IF l_http_status_code = 200 OR l_http_status_code = 201 THEN',
'                IF l_response IS NOT NULL AND LENGTH(TRIM(l_response)) > 0 THEN',
'                    BEGIN',
'                        apex_json.parse(l_response);',
'',
'                        l_access_token := apex_json.get_varchar2(p_path => ''access_token'');',
'                        l_expires_in := apex_json.get_number(p_path => ''expires_in'');',
'',
'                        -- Insert new token into auth_history',
'                        INSERT INTO auth_history (',
'                            accesstoken,',
'                            expiry_in,',
'                            created_by',
'                        ) VALUES (',
'                            l_access_token,',
'                            CAST(SYSDATE AS TIMESTAMP) + NUMTODSINTERVAL(l_expires_in, ''SECOND''),',
'                            ''APEX AUTOMATION''',
'                        );',
'',
'                        DBMS_OUTPUT.put_line(''New token inserted. Token: '' || l_access_token);',
'',
'                    EXCEPTION',
'                        WHEN OTHERS THEN',
'							INSERT INTO auth_history (',
'								accesstoken,',
'								expiry_in,',
'								created_by',
'							) VALUES (',
'								''l_access_token'',',
'								SYSTIMESTAMP,',
'								''ERROR_INNER_EXCEPTION''',
'							);',
'                            DBMS_OUTPUT.put_line(''JSON Parse Error: '' || SQLERRM || '' | Raw Response: '' || SUBSTR(l_response, 1, 1000));',
'                    END;',
'                ELSE',
'					INSERT INTO auth_history (',
'                            accesstoken,',
'                            expiry_in,',
'                            created_by',
'                        ) VALUES (',
'                            ''l_access_token'',',
'                            SYSTIMESTAMP,',
'                            ''RESPONSE_NULL_ELSE''',
'                        );',
'                END IF;',
'            ELSE',
'			 -- Insert new token into auth_history',
'                        INSERT INTO auth_history (',
'                            accesstoken,',
'                            expiry_in,',
'                            created_by',
'                        ) VALUES (',
'                            ''l_access_token'',',
'                            SYSTIMESTAMP,',
'                            ''STATUS_200_ELSE''',
'                        );',
'                DBMS_OUTPUT.put_line(''OAuth API Error: HTTP Status '' || l_http_status_code || '' | Response: '' || SUBSTR(l_response, 1, 500));',
'            END IF;',
'',
'    END;',
'',
'END;',
''))
,p_action_clob_language=>'PLSQL'
,p_location=>'LOCAL'
,p_error_message=>'Failed to refersh access token'
,p_stop_execution_on_error=>true
);
